(()=>{async function e(){const e=document.getElementById("wp-recent-posts");try{const n=await fetch("https://www.eclecticpassions.net/wp-json/wp/v2/posts?per_page=5");if(!n.ok)throw new Error("Network response was not ok");const s=await n.json();let t='<ul class="wp-block-latest-posts__list">';s.forEach(e=>{const n=e.title.rendered,s=e.link;t+=`<li style="padding-right: 1rem;"><a class="wp-block-latest-posts__post-title" href="${s}" target="_blank" rel="noopener">${n}</a></li>`}),t+="</ul>",e.innerHTML=t}catch(t){e.innerHTML="Failed to load recent posts.",console.error(t)}}document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("wp-recent-posts");if(!t)return;const n=new IntersectionObserver((t,n)=>{t[0].isIntersecting&&(e(),n.disconnect())},{threshold:.1});n.observe(t)})})()